import React from "react";

function Event({ id, title, date, isChecked, handleCheckClick }) {
  const timeSlice = date.substring(11, 16);

  return (
    <>
      <div className="eventsArrayDiv">
        <textarea
          className="eventTitle"
          value={title}
          rows="1" // Set rows to 1 to keep it like a single-line input, adjust as necessary
          style={{ width: "100%", resize: "none" }} // Optional styling to adjust the width and prevent resizing
        />
        <div className="eventsArrayDivDates" id={id}></div>
        <div className="eventTime">
          <span className="timeSlice">{timeSlice}</span>
          <input
            className="checkbox-container"
            type="checkbox"
            checked={isChecked}
            size={24}
            value={title}
            onChange={(e) => handleCheckClick(id, e.target.checked)}
            style={{
              height: 20,
              width: 20,
            }}
          />
        </div>
      </div>
    </>
  );
}

export default Event;
